// String.fromCharCode
    console.log(str);
    let a=str.split("");
    console.log(a);
    let res=[];
    for (let i=0;i<a.length;i++){
        // console.log(a[i]);
            let m = a[i].charCodeAt();
            // console.log(m);
            if(m<65 || m>90){
                // console.log(String.fromCharCode(m));
                res.push(String.fromCharCode(m));
            }
            else if(m<78){
                // console.log(String.fromCharCode(m+13));
