function confirmEnding(str, target) {
  // "Never give up and good luck will find you."
  // -- Falcor
  let res=1;
  let arr=[...str];
  let tag=[...target];
  console.log(arr);
  console.log(tag);
  for(let i=1;i<=tag.length;i++){
    console.log(i);
    console.log(arr[arr.length-i]);
    if(arr[arr.length-i]==tag[tag.length-i]){
      res=res*1;
    }
    else{
      res=res*0;
    }
  }
  console.log(res);
  if (res==1){
    return true;
  }
  else{
    return false;
  }
}

