function convertHTML(str) {
  // &colon;&rpar;
  let m=str.split("");
  console.log(m);
  for(let i=0;i<m.length;i++){
    // console.log(m[i]);
    switch(m[i]){
      case "&":
      m[i]="&amp;";
      break;
      case "<":
      m[i]="&lt;";
      break;
      case ">":
      m[i]="&gt;"
      break;
      case '"':
      m[i]="&quot;"
      break;
      case "'":
      m[i]="&apos;"
      break;
    }
  }
  console.log(m);
  return m.join("");
}

