function findLongestWordLength(str) {
  let arr=str.split(" ");
  let len=0;
  console.log(arr);
  for (let i=0;i<arr.length;i++){
    if (arr[i].length>len){
      len=arr[i].length;
    }
  }
  console.log(len);
  return len;
}

console.log(findLongestWordLength("The quick brown fox jumped over the lazy dog"));
