function translatePigLatin(str) {
  let a=[];
  a=str.concat();
  let n=a.search(/[aeiou]/);
  console.log(n);
  if(n==0){
    let x=a.concat("way");
    console.log(x);
    return x;
  }
  else if(n==-1){
    return a.concat("ay");
  }else {
  let m=a.slice(0,n);
  let b=a.slice(n,);
  let c=b.concat(m).concat("ay");
  console.log(c);
  return c;
  }
}

