function myReplace(str, before, after) {
  let a;
  let m=before.search(/[A-Z]/);
  console.log(m);
  a=str.concat();
  console.log(a);
  if (m==-1){
  let b=a.replace(before,after);
  console.log(b);

  return b;}
  else{
    let n=after.slice(0,m+1).toUpperCase()+after.slice(m+1,);

    console.log(n);
    return a.replace(before,n);
  }
}

