function loop(a,b){
  let d=[];
    for(let i=0;i<b.length;i++){
    let c=[];
    for(let j=0;j<a.length;j++){
      if(a[j]%b[i]==0){
        c.push(a[j]);
      }
    }
    console.log(b[i]+":"+c);
