      d.push(b[i]);
      for(let j=0;j<a.length;j++){
      if(a[j]%b[i]==0){
        a[j]=a[j]/b[i];
      }
    }
    }
    console.log("a:"+a);
  }
  return d.reduce((a,b)=>(a*b),1);
}


function prime(num){
  if(num==2){return true}
  let a=[];
  for(let i=1;i<Math.sqrt(num)+1;i++){
    if(num%i==0){
        a.push(i);
    }
  }
  if(a.length>1){
    return false;
  }
  else{
    return true;
  }
}
