  let m=str.replace(/[_]/g,"");
  console.log(m);
  a=m.replace(/[A-Z]|\s/g, function(i){return "-"+i.toLowerCase()});
    console.log(a);
    b=a.split("-");
    console.log(b);
  let c=b.filter(x=>x!="").filter(x=>x!=" ").map(x=>x.replace(/\s+/g,""));

  console.log(c);
  let d=c.join("-");
  console.log(d);
  return d;
}

