function sumPrimes(num) {
  let a=[];
  for (let i=0;i<=num;i++){
    if(isPrime(i)){
      a.push(i);
    }
  }
  console.log(a);
  let sum=a.reduce(function add(a,b){return a+b},0);
  console.log(sum);
  return sum-1;
}
function isPrime(num){
  let a=[];
  for(let i=1;i<=Math.sqrt(num);i++){
    if(num%i==0){
      a.push(i);
    }
  }
  if(a.length>1){
    return false;
  }
  else{
    return true;
  }
}
