function titleCase(str) {
  let arr=str.split(" ");
  let res="";
  for (let i=0;i<arr.length;i++){
      let l=[...arr[i]];
      // console.log(l);
      for (let j=0;j<l.length;j++){
        // console.log(j);
        // console.log(l[j]);
      if(j==0){
        l[j]=l[j].toUpperCase()
      }
      else{
        l[j]=l[j].toLowerCase();
      }
    }
    // console.log(l);
    let word=l.join("");
    console.log(word);
    arr[i]=word;
    // console.log(arr[i]);
  }
  console.log(arr);
  res=arr.join(" ");
  return res;
}

