function truncateString(str, num) {
  // Clear out that junk in your trunk
  let arr=[...str];
  if(arr.length<=num){
    return str;
  }
  let newarr=[];
  console.log(arr);
  newarr=arr.slice(0,num);
  console.log(newarr);
  let s=newarr.join("");
  s=s+"...";
  console.log(s);
  return s;
}

