function getIndexToIns(arr, num) {
  // Find my place in this sorted array.
  arr=arr.sort(function(a,b){
    return a-b;
  });
  console.log(arr);
  for (let i =0;i<arr.length;i++){
    if (num<=arr[i]){
      console.log(i);
      return i;
    }
  }
  return arr.length;
}

